
public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1, num2; // java 變數 不可由數字開頭 example: int 1kk
		num1 = 123;
		num2 = 234;
		int num3 = 333;
		double PI = 3.14159265359;
		boolean bool = false;
		char letter = 'R';

		System.out.println("Hello World!!");
		System.out.println("輸出一首七言絕句");
		System.out.println("岑參《赴北庭度隴思家》\n" + " 西向輪台萬里餘\n 也知鄉信日應疏\n 隴山鸚鵡能言語\n 為報家人數寄書");
		System.out.println("輸出數字");
		System.out.println("Number 1 is " + num1);
		System.out.println("Number 2 is " + num2);
		System.out.println("var int 3 is " + num3);
		System.out.println("var double is " + PI);
		System.out.println("var boolean is " + bool);
		System.out.println("var char is " + letter);

	}

}
